import React from "react";
import { connect } from "react-redux";
import { playTrack, pauseTrack, stopTrack } from "../actions";
import styled from "styled-components";
class LectureBar extends React.Component {
  state = { play: "stop" };
  onClickPlay = () => {
    this.setState({ play: "play" });
    this.props.playTrack();
  };
  onClickPause = () => {
    this.setState({ play: "pause" });
    this.props.pauseTrack();
  };
  onClickStop = () => {
    this.setState({ play: "stop" });
    this.props.stopTrack();
  };

  render() {
    return (
      <div>
        LectureBar
        <button onClick={this.onClickPlay}>Play </button>
        <Button onClick={this.onClickPause}>
          <i className="far fa-pause-circle"></i>
        </Button>
        <button onClick={this.onClickStop}>Stop </button>
      </div>
    );
  }
}
const mapStateToProps = state => {
  return { play: state.play.play };
};
export default connect(mapStateToProps, { playTrack, pauseTrack, stopTrack })(
  LectureBar
);
const Button = styled.button`
    margin: 5px;
    color: #ffdc12 !important;
    text-transform: uppercase;
    text-decoration: none;
    background: #2a2a29;
    width: 50px;
    height: 50px;
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.2), 0 6px 6px rgba(0, 0, 0, 0.2);
    border: 2px solid #ffdc12 !important;
    border-radius: 50%;
    transition: all 0.25s;
    font-size: 25px;
    i {
      margin: auto;
    }
    :hover {
      color: #2a2a29 !important;
      background: #ffdc12;
      border-color: #2a2a29 !important;
    }
  }
`;
