const React = require("react");
class Upload extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      file: null,
    };
    this.handleChange = this.handleChange.bind(this);
  }
  handleChange(event) {
    this.setState({
      file: URL.createObjectURL(event.target.files[0]),
    });
  }
  render() {
    return (
      <div>
        <input type="file" onChange={this.handleChange} accept="audio/mp3" />
        <div>{console.log(this.state.file)}</div>

        <figure>
          <figcaption>Listen to the Drum</figcaption>
          <audio controls src={this.state.file} onPlay={this.onPlay}>
            Your browser does not support the
            <code>audio</code> element.
          </audio>
        </figure>
      </div>
    );
  }
}
module.exports = Upload;
