import React from "react";
import { connect } from "react-redux";
import styled from "styled-components";
class Audio extends React.Component {
  state = {
    duration: null,
    volume: 100,
    savedVolume: 100,
  };
  onChangeSlider = e => {
    this.setState(
      { volume: e.target.value },
      () => (this.audio.volume = this.state.volume / 100)
    );
  };
  handlePlay() {
    if (this.props.play === "play" && this.audio) {
      this.audio.play();
    }
  }

  handlePause() {
    if (this.props.play === "pause" && this.audio) {
      this.audio.pause();
    }
  }
  handleStop() {
    if (this.props.play === "stop" && this.audio) {
      this.audio.currentTime = 0;
      this.audio.pause();
    }
  }
  sendData = () => {
    this.props.parentCallback(this.state.duration);
  };
  onMuteButton = () => {
    if (this.state.volume > 0) {
      return (
        <button
          onClick={() =>
            this.setState({ savedVolume: this.state.volume, volume: 0 })
          }
        >
          Mute
        </button>
      );
    } else
      return (
        <button
          onClick={() =>
            this.setState({
              volume:
                this.state.savedVolume === 0 ? 100 : this.state.savedVolume,
            })
          }
        >
          UnMute
        </button>
      );
  };
  componentDidMount() {
    this.audio.onloadedmetadata = () => {
      console.log(this.audio.volume);
      this.setState(
        { duration: this.audio.duration, volume: this.audio.volume },
        this.sendData
      );
    };
  }

  render() {
    console.log(this.state.volume);
    return (
      <div>
        {this.handlePlay()}
        {this.handleStop()}
        {this.handlePause()}
        <audio
          ref={audio => {
            this.audio = audio;
          }}
          src={this.props.src}
        />

        <p>
          {this.onMuteButton()}
          <Slider
            onChange={this.onChangeSlider}
            defaultValue="100"
            type="range"
            name="points"
            min="0"
            max="100"
          />
          {this.state.volume}%
        </p>
      </div>
    );
  }
}
const mapStateToProps = state => {
  return { play: state.play.play };
};
export default connect(mapStateToProps)(Audio);

const Slider = styled.input`
  position: absolute;
  bottom: 5%;
  left: 0;
  appearance: none;
  height: 25px;
  outline: none;
  width: 80%;
  opacity: 0.7;
  transition: opacity 0.2s;
  background: #02aab0;
  ::-webkit-slider-thumb {
    -webkit-appearance: none;
    appearance: none;
    width: 25px;
    height: 25px;
    background: black;
    cursor: pointer;
  }
  ::-moz-range-thumb {
    width: 25px;
    height: 25px;
    background: black;
    cursor: pointer;
  }
  :hover {
    opacity: 1;
  }
`;
