import React from "react";
import { connect } from "react-redux";
class Audio extends React.Component {
  state = {
    duration: null,
    volume: 100,
  };
  onChangeSlider() {
    this.audio.volume = e.target.value / 100;
  }
  handlePlay() {
    if (this.props.play === "play" && this.audio) {
      this.audio.play();
    }
  }

  handlePause() {
    if (this.props.play === "pause" && this.audio) {
      this.audio.pause();
    }
  }
  handleStop() {
    if (this.props.play === "stop" && this.audio) {
      this.audio.currentTime = 0;
      this.audio.pause();
    }
  }
  sendData = () => {
    this.props.parentCallback(this.state.duration);
  };
  componentDidMount() {
    this.audio.onloadedmetadata = () => {
      console.log(this.audio.volume);
      this.setState(
        { duration: this.audio.duration, volume: this.audio.volume },
        this.sendData
      );
    };
  }

  render() {
    console.log(this.props.play);
    return (
      <div>
        {this.handlePlay()}
        {this.handleStop()}
        {this.handlePause()}
        <audio
          ref={audio => {
            this.audio = audio;
          }}
          src={this.props.src}
        />

        <p>
          <input
            ref={slider => {
              this.slider = slider;
            }}
            defaultValue="100"
            type="range"
            name="points"
            min="0"
            max="100"
          />
        </p>
      </div>
    );
  }
}
const mapStateToProps = state => {
  return { play: state.play.play };
};
export default connect(mapStateToProps)(Audio);
