import React from "react";
import styled from "styled-components";
import { connect } from "react-redux";
import Filebox from "./Filebox";
import { AddIcon } from "./styleSheet";
class BoxSpawner extends React.Component {
  state = { boxCount: 2, boxes: [0, 1] };
  renderAddButton = () => {
    if (Object.keys(this.props.tracks).length >= this.state.boxCount) {
      return (
        <div>
          <AddIcon>
            <i className="fas fa-plus"></i>
            Add
          </AddIcon>
        </div>
      );
    }
  };
  renderBoxes = () => {
    this.state.boxes.forEach(e => {
      return <Filebox key={e} />;
    });
  };
  render() {
    console.log(this.state.boxes);
    console.log(Object.keys(this.props.tracks).length);
    return (
      <Wrapper>
        {this.renderBoxes()}
        {this.renderAddButton()}
      </Wrapper>
    );
  }
}
const mapStateToProps = state => {
  return { tracks: state.tracks };
};
export default connect(mapStateToProps)(BoxSpawner);
const Wrapper = styled.div`
  display: flex;
  flex-wrap: wrap;
  div {
    flex: 0 48%;
  }
  @media screen and (max-width: 900px) {
    flex-direction: column;
  }
`;
