import React from "react";
import { connect } from "react-redux";
import { playTrack, pauseTrack, stopTrack } from "../actions";
class LectureBar extends React.Component {
  state = { play: "stop" };
  onClickPlay = () => {
    this.setState({ play: "play" });
    this.props.playTrack();
  };

  render() {
    return (
      <div>
        LectureBar
        <button onClick={this.onClickPlay}>Play </button>
        <button onClick={this.onClickPlay}>Pause </button>
        <button onClick={this.onClickPlay}>Stop </button>
      </div>
    );
  }
}
const mapStateToProps = state => {
  return { play: state.play.play };
};
export default connect(mapStateToProps, { playTrack, pauseTrack, stopTrack })(
  LectureBar
);
