import Upload from "./Upload";
import Audio from "./Audio";
class Filebox extends React.Component {
  state = { fileName: null, duration: null };
  callbackUpload = data => {
    this.setState({ fileName: data });
  };
  render() {
    console.log("fileName", this.state);
