import { combineReducers } from "redux";
import _ from "lodash";
const INITIAL_STATE = { fileUrl: "LeDZEP", duration: "3:40" };
export const trackListReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case "ADD_TRACK":
      return { ...state, [action.payload.fileUrl]: action.payload };
    case "REMOVE_TRACK":
      return _.omit(state, action.payload);

    case "FETCH_TRACKS":
      return { ...state, ..._.mapKeys(action.payload, "filename") };
    default:
      return state;
  }
};

export const playTrackReducer = (state = null, action) => {
  switch (action.type) {
    case "PLAY_TRACKS":
      return { ...state, play: true };
    case "PAUSE_TRACKS":
      return { ...state, play: false };
    case "STOP_TRACKS":
      return { ...state, play: false };
    default:
      return state;
  }
};
export default combineReducers({
  tracks: trackListReducer,
  play: playTrackReducer,
});
