import React from "react";
import Filebox from "./Filebox";
class App extends React.Component {
  state = { onPlay: false };
  render() {
    console.log("app onplay :", this.state.onPlay);
    return (
      <div>
        <Filebox onPlay={this.state.onPlay} />
        <Filebox onPlay={this.state.onPlay} />
        <button onClick={this.setState({ onPlay: true })}>Play </button>
      </div>
    );
  }
}

export default App;
