import { combineReducers } from "redux";
//import _ from "lodash";
export const trackListReducer = () => {
  return { title: "Ledzep", duration: "3:40" };
};
const INITIAL_STATE = { play: null };
export const playTrackReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case "PLAY_TRACKS":
      return { ...state, play: true };
    case "PAUSE_TRACKS":
      return { ...state, play: false };
    case "STOP_TRACKS":
      return { ...state, play: false };
    default:
      return state;
  }
};
export default combineReducers({
  tracks: trackListReducer,
  play: playTrackReducer,
});
