import React from "react";
import { connect } from "react-redux";
class Upload extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      fileNameName: null,
    };
    this.handleChange = this.handleChange.bind(this);
  }
  sendData = () => {
    this.props.parentCallback(this.state.fileName);
  };
  handleChange = e => {
    this.setState(
      {
        fileName: URL.createObjectURL(e.target.files[0]),
        Track: e.target,
      },
      this.sendData
    );
  };
  render() {
    return (
      <div>
        <input type="file" onChange={this.handleChange} accept="audio/mp3" />
        <div></div>
      </div>
    );
  }
}
const mapStateToProps = state => {
  return { tracklist: state.tracks };
};
export default connect(mapStateToProps, {})(Upload);
