import React from "react";
import { connect } from "react-redux";
class LectureBar extends React.Component {
  state = { play: false };
  render() {
    return (
      <div>
        LectureBar
        <button onClick={() => this.setState({ onPlay: true })}>Play </button>
      </div>
    );
  }
}
mapStateToProps = state => {
  return { play: state.play };
};
export default connect(mapStateToProps, {})(LectureBar);
