import React from "react";
import Upload from "./Upload";
import Audio from "./Audio";
import { addTrack } from "../actions";
import { connect } from "react-redux";
class Filebox extends React.Component {
  state = { fileUrl: null, fileName: null, duration: null, added: false };
  callbackUpload = (fileUrl, fileName) => {
    this.setState({ fileUrl, fileName });
  };
  callbackAudio = duration => {
    this.setState({ duration });
  };
  addToTrackList = () => {
    if (this.state.fileUrl && this.state.duration && !this.state.added) {
      this.props.addTrack;
    }
  };
  componentDidUpdate = () => {
    this.addToTrackList();
  };
  render() {
    // !verifty same duration
    return (
      <div>
        <Upload
          onPlay={this.props.onPlay}
          parentCallback={this.callbackUpload}
        />
        <Audio
          src={this.state.fileUrl}
          onPlay={this.props.onPlay}
          parentCallback={this.callbackAudio}
        />
      </div>
    );
  }
}
const mapStateToProps = state => {
  return { tracks: state.tracks };
};
export default connect(mapStateToProps, { addTrack })(Filebox);
