export const playTrack = play => {
  return { type: "PLAY_TRACK", payload: play };
};
export const pauseTrack = play => {
  return { type: "PAUSE_TRACK", payload: play };
};
export const stopTrack = play => {
  return { type: "STOP_TRACK", payload: play };
};

export const addTrack = (fileUrl, duration) => {
  return { type: "ADD_TRACK", payload: { patacaisse: fileUrl, duration } };
};
export const removeTrack = (filename, duration) => {
  return { type: "REMOVE_TRACK", payload: { filename, duration } };
};
export const fetchTracks = (filename, duration) => {
  return { type: "FETCH_TRACKS" };
};
